/*Global Message types.*/enum {		/* Object creation */	NEW = 0, DELETE,			/* Window messages */	DRAG, GROW, CONTENT, KEY_DOWN, ACTIVATE, UPDATE, CLOSE, IDLE,			/* Edit messages */	UNDO, CUT, COPY, PASTE, CLEAR,			/* File I/O */	NEW_FILE, OPEN, CLOSE_FILE, SAVE, SAVE_AS, REVERT, PRINT,		/* Read/eval support */	READ_STREAM, PRINT_STREAM,		MAX_MSG	};typedef long (***Object)();#define SendMsg(obj, msg) (*( (* (Object) obj ) [msg]))#define z(msg) SendMsg(me, msg)