/*2003.03.26	jht		Outline tabbed text file.*/#pragma once#include <LVariableArray.h>#include <Files.h>#include <LOutlineItem.h>#include "CFileItem.h"//typedef LOutlineItem *LOutlineItemPtr;class CTextLineItem;typedef CTextLineItem *CTextLineItemPtr;class CTextFileStore : public LVariableArray{public:						CTextFileStore(CFileItem *fileItem);	virtual 			~CTextFileStore();	void				ExpandFirstLevel();		void				LoadScriptTextFile();		void				InsertOneLine( long lineCount, char *lineStart, char *lineEnd, CTextLineItemPtr &lastItem );	protected:	CFileItem			*mFileItem;	static const char			mNewLineChar = '\r';	static const char			mTabChar = '\t';	static const char			mSpaceChar = ' ';		static inline Boolean IsWhiteChar(char ch) { return (ch==mTabChar) || (ch==mSpaceChar); }	static inline Boolean IsNewLineChar(char ch) { return (ch==mNewLineChar) ; }private:	friend class CFileItem;friend class CTextLineItem;};